sqlMap.select:
/* expend/np/user/NpUserReportSQL.xml - NpUserReportA.selectEAPCardReport */ /* 카드사용현황 - 영리 */ /* 전자결재(영리) 관리자 카드사용 현황 */ SELECT A.sync_id AS syncId , B.card_code AS cardCode , B.card_name AS cardName , B.card_num AS cardNum , A.auth_num AS authNum , A.auth_date AS authDate , A.auth_time AS authTime , A.georae_coll AS georaeColl , A.georae_stat AS georaeStat , CASE WHEN A.GEORAE_STAT = '1' || A.GEORAE_STAT = 'N' THEN '승인' ELSE '취소' END AS georaeStatName , A.georae_cand AS georaeCand , IFNULL(A.request_amount,0) AS requestAmount , IFNULL(A.amt_md_amount,0) AS amtMdAmount , IFNULL(A.vat_md_amount,0) AS vatMdAmount , IFNULL(A.ser_amount,0) AS serAmt , A.merc_name AS mercName , A.merc_saup_no AS mercSaupNo , A.send_yn AS sendYN , A.if_m_id AS ifMId , A.if_d_id AS ifDId , IFNULL(C.doc_seq, '') AS docSeq , IFNULL(C.doc_title, '') AS docTitle , IFNULL(C.doc_no, '') AS docNo , IFNULL(C.form_id, '') AS formSeq FROM ( SELECT * FROM $DB_NEOS$t_ex_card_aq_tmp WHERE auth_date BETWEEN #fromDate# AND #toDate# AND merc_name LIKE CONCAT('%',#searchMercName#,'%')
AND IFNULL(send_yn,'N') = 'Y' AND if_m_id = 'd'
AND IFNULL(send_yn,'N') LIKE CONCAT('%',#sendYN#,'%')
) A INNER JOIN ( SELECT C.card_code , C.card_num , C.card_name , REPLACE(C.card_num,'-','') AS repCardNum FROM $DB_NEOS$t_ex_card C INNER JOIN $DB_NEOS$t_ex_card_public A ON C.comp_seq = A.comp_seq AND REPLACE(REPLACE(C.card_num,'-',''),' ','') = REPLACE(REPLACE(A.card_num,'-',''),' ','') INNER JOIN ( SELECT B.gbn_org AS org_div , B.dept_seq AS org_id FROM ( SELECT path FROM $DB_NEOS$v_org_chart_child WHERE dept_seq = #deptSeq# ) A INNER JOIN ( SELECT gbn_org , dept_seq , path FROM $DB_NEOS$v_org_chart_child ) B ON A.path LIKE CONCAT('%', B.path, '%') UNION SELECT 'u' AS org_div , #empSeq# AS org_id ) B ON A.org_div = B.org_div AND A.org_id = B.org_id WHERE C.comp_seq = #compSeq# AND C.card_num LIKE CONCAT('%',#searchCardNum#,'%') ) B ON A.card_num = B.repCardNum LEFT OUTER JOIN ( SELECT A.doc_seq , B.doc_title , B.doc_no , B.form_id , A.interface_seq FROM ( SELECT A.res_doc_seq , A.doc_seq , B.interface_seq FROM $DB_NEOS$t_Exnp_resdoc A INNER JOIN $DB_NEOS$t_exnp_restrade B ON B.interface_type = 'card' AND A.res_doc_seq = B.res_doc_seq WHERE doc_seq != '' AND doc_status IN ( '001', '002', '003', '004', '006', '008', '009', '20', '30', '90' ) ) A INNER JOIN $DB_NEOS$teag_appdoc B ON A.doc_seq = B.doc_id ) C ON A.sync_id = C.interface_seq
ORDER BY A.auth_date, A.auth_time, sync_id ASC
ORDER BY A.auth_date, A.auth_time, sync_id DESC

sqlMap.update:
/* expend/np/user/NpUserReportSQL.xml - NpUserReportA.updateConsConfferStatus */
/* 결의서 반환 / 취소 진행 */	
UPDATE	$DB_NEOS$t_exnp_consdoc
SET	conffer_return_yn 			= #confferReturn# 
	, conffer_return_name 		= #empName#
	, conffer_return_emp_seq 	= #empSeq#
WHERE	comp_seq 		= #compSeq#
 AND	cons_doc_seq 	IN ($consDocSeqs$);
