mapper.select:
/*	[iCUBE][MSSQL] AnguCode.getAnguTermInfoI_Select	*/

/*	BOJO_RMK : 보조금통장표시	*/
/*	TR_RMK : 거래처통장표시	*/

/*	EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_TERM_SELECT'	*/

/*	USP_ANGU300_TERM_SELECT : result sample	*/

/*	────────┬───────────────────┬───────────────	*/
/*	CO_CD	│	BOJO_RMK		│	TR_RMK			*/
/*	────────┼───────────────────┼───────────────	*/
/*	3585	│	보조금통장표			│	거래처통장표			*/
/*	────────┴───────────────────┴───────────────	*/

DECLARE @RESULT TABLE(
	CO_CD NVARCHAR(50)
	/* , BOJO_RMK NVARCHAR(20) */
	, TR_RMK NVARCHAR(20)
)

INSERT INTO @RESULT ( CO_CD, /* BOJO_RMK, */ TR_RMK )
EXEC USP_ANGU300_TERM_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD}

SELECT	CO_CD AS CO_CD /* ERP 회사 코드 */
		/* , BOJO_RMK AS BOJO_RMK */ /* 보조금통장표시내용 */
		, TR_RMK AS TR_RMK /* 거래처통장표시내용 */
FROM	@RESULT

/* [iCUBE][MSSQL] AnguCode.getAnguBusinessInfoI_Select */
/* 국고보조통합시스템 - 국고보조사업 코드 조회 */
/* anguBusinessCode : 국고보조사업 코드 */
/* anguBusinessName : 국고보조사업 명칭 */

/* EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_DDTLBZ_CD_SELECT' */

/*	USP_ANGU300_DDTLBZ_CD_SELECT : result sample	*/
/*	────────────┬────────────────	*/
/*	DDTLBZ_ID	│	DDTLBZ_NM		*/
/*	────────────┼────────────────	*/
/*	TEST-00001	│	상세내역사업1	*/
/*	────────────┼────────────────	*/
/*	TEST-00002	│	상세내역사업2	*/
/*	────────────┼────────────────	*/
/*	TEST-00003	│	상세내역사업3	*/
/*	────────────┼────────────────	*/
/*	TEST-00004	│	상세내역사업4	*/
/*	────────────┼────────────────	*/
/*	TEST-00005	│	상세내역사업5	*/
/*	────────────┼────────────────	*/
/*	TEST-00006	│	상세내역사업6	*/
/*	────────────┼────────────────	*/
/*	TEST-00007	│	상세내역사업7	*/
/*	────────────┴────────────────	*/

/*	DDTLBZ_ID > anguBusinessCode	*/
/*	DDTLBZ_NM > anguBusinessName	*/

DECLARE @RESULT TABLE(
	anguBusinessCode NVARCHAR(20)
	, anguBusinessName NVARCHAR(300)
)

INSERT INTO @RESULT ( anguBusinessCode, anguBusinessName )
EXEC USP_ANGU300_DDTLBZ_CD_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD},@BSNSYEAR=#{BSNSYEAR}

SELECT	anguBusinessCode /* 국고보조사업 코드 */
		, anguBusinessName /* 국고보조사업 명 */
FROM	@RESULT

/* [iCUBE][MSSQL] AnguCode.getAnguAccountDivInfoI_Select */
/* 국고보조통합시스템 - 이체계좌구분 코드 조회 */
/* anguAccountDivCode (CMMN_DETAIL_CODE) : 이체계좌구분 코드 */
/* anguAccountDivName (CMMN_DETAIL_CODE_NM) : 이체계좌구분 명칭 */

/* EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_CMMN_CD_SELECT' */

/*	USP_ANGU300_CMMN_CD_SELECT : result sample	*/
/*	────────────┬───────────────┬───────────────────────┬───────────────────────┬──────────────────────	*/
/*	SYS_SE_CODE	│	CMMN_CODE	│	CMMN_CODE_NM		│	CMMN_DETAIL_CODE	│	CMMN_DETAIL_CODE_NM	*/
/*	────────────┼───────────────┼───────────────────────┼───────────────────────┼──────────────────────	*/
/*	001			│	1089		│	이체계좌구분코드			│	001					│	거래처계좌로이체			*/
/*	────────────┼───────────────┼───────────────────────┼───────────────────────┼──────────────────────	*/
/*	001			│	1089		│	이체계좌구분코드			│	002					│	보조금계좌로이체			*/
/*	────────────┴───────────────┴───────────────────────┴───────────────────────┴──────────────────────	*/

/*	CMMN_DETAIL_CODE > anguAccountDivCode		*/
/*	CMMN_DETAIL_CODE_NM > anguAccountDivName	*/

DECLARE @RESULT TABLE(
	SYS_SE_CODE NVARCHAR(3)
	, CMMN_CODE NVARCHAR(10)
	, CMMN_CODE_NM NVARCHAR(200)
	, anguAccountDivCode NVARCHAR(10)
	, anguAccountDivName NVARCHAR(200)
)

INSERT INTO @RESULT ( SYS_SE_CODE, CMMN_CODE, CMMN_CODE_NM, anguAccountDivCode, anguAccountDivName )
exec USP_ANGU300_CMMN_CD_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD},@CMMN_CODE=#{CMMN_CODE}

SELECT	anguAccountDivCode /* 이쳬계좌구분 코드 */
		, anguAccountDivName /* 이체계좌구분 명 */
FROM	@RESULT

		/* [iCUBE][MSSQL] AnguCode.getAnguAccountReasonDivInfoI_Select */
		/* 국고보조통합시스템 - 자계좌이체사유 코드 조회 */
		/* anguAccountReasonDivCode (CMMN_DETAIL_CODE) : 자계좌이체사유 코드 */
		/* anguAccountReasonDivName (CMMN_DETAIL_CODE_NM) : 자계좌이체사유 명칭 */
		
		/* EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_CMMN_CD_SELECT' */
		
		/*	USP_ANGU300_CMMN_CD_SELECT : result sample	*/

		/*	────────────┬───────────────┬───────────────────────────┬───────────────────────┬───────────────────────────────	*/
		/*	SYS_SE_CODE	│	CMMN_CODE	│	CMMN_CODE_NM			│	CMMN_DETAIL_CODE	│	CMMN_DETAIL_CODE_NM				*/
		/*	────────────┼───────────────┼───────────────────────────┼───────────────────────┼───────────────────────────────	*/
		/*	001			│	0665		│	보조금계좌이체사유코드			│	001					│	보조금교부전집행내역 상계					*/
		/*	────────────┼───────────────┼───────────────────────────┼───────────────────────┼───────────────────────────────	*/			
		/*	001			│	0665		│	보조금계좌이체사유코드			│	002					│	인건비(원천징수 후 개별지급)				*/
		/*	────────────┼───────────────┼───────────────────────────┼───────────────────────┼───────────────────────────────	*/
		/*	001			│	0665		│	보조금계좌이체사유코드			│	003					│	해외송금(송금증첨부 필수)					*/
		/*	────────────┼───────────────┼───────────────────────────┼───────────────────────┼───────────────────────────────	*/
		/*	001			│	0665		│	보조금계좌이체사유코드			│	004					│	카드결제							*/
		/*	────────────┼───────────────┼───────────────────────────┼───────────────────────┼───────────────────────────────	*/
		/*	001			│	0665		│	보조금계좌이체사유코드			│	005					│	자체시스템으로 이체						*/
		/*	────────────┼───────────────┼───────────────────────────┼───────────────────────┼───────────────────────────────	*/
		/*	001			│	0665		│	보조금계좌이체사유코드			│	099					│	기타								*/
		/*	────────────┴───────────────┴───────────────────────────┴───────────────────────┴───────────────────────────────	*/
		
		/*	CMMN_DETAIL_CODE > anguAccountReasonDivCode		*/
		/*	CMMN_DETAIL_CODE_NM > anguAccountReasonDivName	*/
		
		DECLARE @RESULT TABLE(
			SYS_SE_CODE NVARCHAR(3)
			, CMMN_CODE NVARCHAR(10)
			, CMMN_CODE_NM NVARCHAR(200)
			, anguAccountReasonDivCode NVARCHAR(10)
			, anguAccountReasonDivName NVARCHAR(200)
		)
		
		INSERT INTO @RESULT ( SYS_SE_CODE, CMMN_CODE, CMMN_CODE_NM, anguAccountReasonDivCode, anguAccountReasonDivName )
		exec USP_ANGU300_CMMN_CD_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD},@CMMN_CODE=#{CMMN_CODE}
		
		SELECT	anguAccountReasonDivCode /* 자계좌이체사유 코드 */
				, anguAccountReasonDivName /* 자계좌이체 사유 명 */
		FROM	@RESULT

/*	[iCUBE][MSSQL] AnguCode.getAnguEtaxTaxInfoI_Select	*/
/*	국고보조통합시스템 - 매입전자세금계산서 과세 조회	*/

/*	anguEtaxTaxNo : No						*/
/*	anguEtaxTaxDiv : 분류					*/
/*	anguEtaxTaxWriteDate : 작성일자			*/
/*	anguEtaxTaxIssueDate : 발급일자			*/
/*	anguEtaxTaxPartnerName : 거래처명		*/
/*	anguEtaxTaxPartnerNum : 거래처사업번호	*/
/*	anguEtaxTaxAmt : 공급가액				*/
/*	anguEtaxTaxVat : 부가세					*/
/*	anguEtaxTaxReqAmt : 합계액				*/
/*	anguEtaxTaxNum : 전자세금계산서승인번호	*/
/*	anguEtaxTaxUsedAmt : 기사용금액			*/
/*	anguEtaxTaxNotUsedAmt : 등록가능금액	*/

/*	EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_ETAX_SELECT'	*/

/*	USP_ANGU300_ETAX_SELECT : result sample	*/
/*	────────────────────────────┬───────────┬───────────┬───────────────────────┬───────────────┬───────────┬───────────────┬───────────────────────┬───────────────┬───────────────────┬───────────────────┬───────────────────┬───────────────┬───────────────┬───────────┬───────────────────────┬───────────────────┬───────────────────────┬───────────────────────────────┬───────────────────┬───────────────────┬───────────────────┬───────────┬───────────────────────┬───────────────┬───────────────┬───────────────┬──────────	*/
/*	ISS_NO						│	CO_CD	│	DIV_CD	│	DIV_NM				│	ISS_DT		│	TAX_TY	│	ISU_DT		│	TR_NM				│	TRREG_NB	│	SUP_AM			│	VAT_AM			│	SUM_AM			│	DIVCEO_NM	│	ETAX_TY_NM	│	TR_CD	│	BCNC_CMPNY_NM		│	DUZON_BANK_CODE	│	BCNC_ACNUT_NO		│	BCNC_ADRES					│	BCNC_RPRSNTY_NM	│	BCNC_TELNO		│	BCNC_BANK_CODE	│	BANK_NM	│	EXCUT_SUM_AMOUNT	│	AM			│	SUP_M_SPLPC	│	VAT_M_VAT	│	SEND_YN	*/
/*	────────────────────────────┼───────────┼───────────┼───────────────────────┼───────────────┼───────────┼───────────────┼───────────────────────┼───────────────┼───────────────────┼───────────────────┼───────────────────┼───────────────┼───────────────┼───────────┼───────────────────────┼───────────────────┼───────────────────────┼───────────────────────────────┼───────────────────┼───────────────────┼───────────────────┼───────────┼───────────────────────┼───────────────┼───────────────┼───────────────┼──────────	*/
/*	201702011000000028135141	│	3585	│	1000	│	엔프롬정보기술(주)			│	20170101	│	2		│	20170101	│	엔프롬정보기술(주)			│	1111111222	│	31788173.0000	│	3178810.0000	│	34966983.0000	│	이준협			│	일반			│	10000	│	엔프롬정보기술(주)			│	030				│	100112312313		│	서울 강남구 청담동 123-11			│	민병진				│	02-633-5225		│	003				│	기업		│	193100				│	34773883	│	31597173	│	3176710		│	003		*/
/*	────────────────────────────┼───────────┼───────────┼───────────────────────┼───────────────┼───────────┼───────────────┼───────────────────────┼───────────────┼───────────────────┼───────────────────┼───────────────────┼───────────────┼───────────────┼───────────┼───────────────────────┼───────────────────┼───────────────────────┼───────────────────────────────┼───────────────────┼───────────────────┼───────────────────┼───────────┼───────────────────────┼───────────────┼───────────────┼───────────────┼──────────	*/
/*	2017010141000009c0003o07	│	3585	│	1000	│	용당세관				│	20170102	│	2		│	20170102	│	용당세관				│	1231231231	│	22000.0000		│	2200.0000		│	24200.0000		│	용당세관장		│	일반			│	10001	│	용당세관				│	110				│	21323123123123		│	경남 함양군 독계리 222				│	용당세관장			│	055-525-5225	│	011				│	농협중		│	1100				│	23100		│	21000		│	2100		│	003		*/
/*	────────────────────────────┼───────────┼───────────┼───────────────────────┼───────────────┼───────────┼───────────────┼───────────────────────┼───────────────┼───────────────────┼───────────────────┼───────────────────┼───────────────┼───────────────┼───────────┼───────────────────────┼───────────────────┼───────────────────────┼───────────────────────────────┼───────────────────┼───────────────────┼───────────────────┼───────────┼───────────────────────┼───────────────┼───────────────┼───────────────┼──────────	*/
/*	201702021000000029043804	│	3585	│	1000	│	(주)펌앤펌				│	20170103	│	2		│	20170103	│	(주)펌앤펌				│	1388160239	│	22000.0000		│	2200.0000		│	24200.0000		│	박매희			│	일반			│	10006	│	(주)펌앤펌				│	040				│	7777-77777-777777	│	충남 태안군 123-123				│	박매희				│	0419652584		│	004				│	국민		│	NULL				│	24200		│	22000		│	2200		│			*/
/*	────────────────────────────┴───────────┴───────────┴───────────────────────┴───────────────┴───────────┴───────────────┴───────────────────────┴───────────────┴───────────────────┴───────────────────┴───────────────────┴───────────────┴───────────────┴───────────┴───────────────────────┴───────────────────┴───────────────────────┴───────────────────────────────┴───────────────────┴───────────────────┴───────────────────┴───────────┴───────────────────────┴───────────────┴───────────────┴───────────────┴──────────	*/

/*	ETAX_TY_NM			> anguEtaxTaxDiv			*/
/*	ISS_DT 				> anguEtaxTaxWriteDate		*/
/*	ISU_DT 				> anguEtaxTaxIssueDate		*/
/*	TR_NM				> anguEtaxTaxPartnerName	*/
/*	TRREG_NB			> anguEtaxTaxPartnerNum		*/
/*	SUP_AM 				> anguEtaxTaxAmt			*/
/*	VAT_AM 				> anguEtaxTaxVat			*/
/*	SUM_AM 				> anguEtaxTaxReqAmt			*/
/*	ISS_NO 				> anguEtaxTaxNum			*/
/*	EXCUT_SUM_AMOUNT	> anguEtaxTaxUsedAmt		*/
/*	AM					> anguEtaxTaxNotUsedAmt		*/

/*	작성일자 : 20170101 ~ 20170921 ( DT_FG : 1 )			*/
/*	발행일자 : 20170101 ~ 20170921 ( DT_FG : 2 )			*/
/*	세무구분 : 과세 ( TAX_TY : 2 )							*/
/*	세무구분 : 면세 ( TAX_TY : 4 )							*/
/*	세금계산서분류 : 전체 ( ETAX_TY : '' )					*/
/*	세금계산서분류 : 일반 ( ETAX_TY : '1' )					*/
/*	세금계산서분류 : 일반 ( ETAX_TY : '2' )					*/
/*	매입거래처 : 거래처코드 ( TR_CD : 거래처코드 + '|' )	*/

DECLARE @RESULT TABLE(
	anguEtaxTaxNum NVARCHAR(30)
	, CO_CD NVARCHAR(4)
	, DIV_CD NVARCHAR(4)
	, DIV_NM NVARCHAR(50)
	, anguEtaxTaxWriteDate NVARCHAR(8)
	, TAX_TY NVARCHAR(1)
	, anguEtaxTaxIssueDate NVARCHAR(8)
	, anguEtaxTaxPartnerName NVARCHAR(60)
	, anguEtaxTaxPartnerNum NVARCHAR(100)
	, anguEtaxTaxAmt NUMERIC(17,4)
	, anguEtaxTaxVat NUMERIC(17,4)
	, anguEtaxTaxReqAmt NUMERIC(17,4)
	, DIVCEO_NM NVARCHAR(20)
	, anguEtaxTaxDiv NVARCHAR(10)
	, TR_CD NVARCHAR(10)
	, BCNC_CMPNY_NM NVARCHAR(300)
	, DUZON_BANK_CODE NVARCHAR(10)
	, BCNC_ACNUT_NO NVARCHAR(108)
	, BCNC_ADRES NVARCHAR(500)
	, BCNC_RPRSNTY_NM NVARCHAR(100)
	, BCNC_TELNO NVARCHAR(14)
	, BCNC_BANK_CODE NVARCHAR(3)
	, BANK_NM NVARCHAR(100)
	, anguEtaxTaxUsedAmt NUMERIC(17,4)
	, anguEtaxTaxNotUsedAmt NUMERIC(17,4)
	, SUP_M_SPLPC NUMERIC(17,4)
	, VAT_M_VAT NUMERIC(17,4)
	, SEND_YN NVARCHAR(3)
)

INSERT INTO @RESULT ( anguEtaxTaxNum, CO_CD, DIV_CD, DIV_NM, anguEtaxTaxWriteDate, TAX_TY, anguEtaxTaxIssueDate, anguEtaxTaxPartnerName, anguEtaxTaxPartnerNum, anguEtaxTaxAmt, anguEtaxTaxVat, anguEtaxTaxReqAmt, DIVCEO_NM, anguEtaxTaxDiv, TR_CD, BCNC_CMPNY_NM, DUZON_BANK_CODE, BCNC_ACNUT_NO, BCNC_ADRES, BCNC_RPRSNTY_NM, BCNC_TELNO, BCNC_BANK_CODE, BANK_NM, anguEtaxTaxUsedAmt, anguEtaxTaxNotUsedAmt, SUP_M_SPLPC, VAT_M_VAT, SEND_YN )
EXEC USP_ANGU300_ETAX_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD},@DT_FG=#{DT_FG},@FR_DT=#{FR_DT},@TO_DT=#{TO_DT},@TAX_TY='2',@ETAX_TY='${ETAX_TY}',@TR_CD=#{TR_CD},@DDTLBZ_ID=#{DDTLBZ_ID},@TASC_CMMN_DETAIL_CODE_NM=#{TASC_CMMN_DETAIL_CODE_NM}

SELECT	CONVERT ( NVARCHAR(10), ROW_NUMBER() OVER(ORDER BY anguEtaxTaxWriteDate, anguEtaxTaxIssueDate, anguEtaxTaxPartnerName) ) AS anguEtaxTaxNo
		, anguEtaxTaxNum
		, CO_CD
		, DIV_CD
		, DIV_NM
		, CONVERT ( NVARCHAR(10), CONVERT ( DATETIME, anguEtaxTaxWriteDate, 112 ), 121 ) AS anguEtaxTaxWriteDate
		, TAX_TY
		, CONVERT ( NVARCHAR(10), CONVERT ( DATETIME, anguEtaxTaxIssueDate, 112 ), 121 ) AS anguEtaxTaxIssueDate
		, anguEtaxTaxPartnerName
		, anguEtaxTaxPartnerNum
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, anguEtaxTaxAmt ), 1 ), '.00', '' ) AS anguEtaxTaxAmt
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, anguEtaxTaxVat ), 1 ), '.00', '' ) AS anguEtaxTaxVat
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, anguEtaxTaxReqAmt ), 1 ), '.00', '' ) AS anguEtaxTaxReqAmt
		, DIVCEO_NM
		, anguEtaxTaxDiv
		, TR_CD /* 거래처 코드 */
		, BCNC_CMPNY_NM
		, DUZON_BANK_CODE /* 은행 코드 */
		, BCNC_ACNUT_NO /* 거래처 사업자/주민번호 */
		, BCNC_ADRES /* 거래처 주소 */
		, BCNC_RPRSNTY_NM
		, BCNC_TELNO /* 거래처 전화번호 */
		, BCNC_BANK_CODE /* 은행 코드 */
		, BANK_NM /* 은행 명 */
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, ISNULL(anguEtaxTaxUsedAmt, 0.0000) ), 1 ), '.00', '' ) AS anguEtaxTaxUsedAmt
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, anguEtaxTaxNotUsedAmt ), 1 ), '.00', '' ) AS anguEtaxTaxNotUsedAmt
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, SUP_M_SPLPC ), 1 ), '.00', '' ) AS SUP_M_SPLPC
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, VAT_M_VAT ), 1 ), '.00', '' ) AS VAT_M_VAT
		, SEND_YN
FROM	@RESULT

/*	[iCUBE][MSSQL] AnguCode.getAnguEtaxTaxFreeInfoI_Select	*/
/*	국고보조통합시스템 - 매입전자세금계산서 과세 조회	*/

/*	anguEtaxTaxNo : No						*/
/*	anguEtaxTaxDiv : 분류					*/
/*	anguEtaxTaxWriteDate : 작성일자			*/
/*	anguEtaxTaxIssueDate : 발급일자			*/
/*	anguEtaxTaxPartnerName : 거래처명		*/
/*	anguEtaxTaxPartnerNum : 거래처사업번호	*/
/*	anguEtaxTaxAmt : 공급가액				*/
/*	anguEtaxTaxVat : 부가세					*/
/*	anguEtaxTaxReqAmt : 합계액				*/
/*	anguEtaxTaxNum : 전자세금계산서승인번호	*/
/*	anguEtaxTaxUsedAmt : 기사용금액			*/
/*	anguEtaxTaxNotUsedAmt : 등록가능금액	*/

/*	EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_ETAX_SELECT'	*/

/*	USP_ANGU300_ETAX_SELECT : result sample	*/
/*	────────────────────────────┬───────────┬───────────┬───────────────────────┬───────────────┬───────────┬───────────────┬───────────────────────┬───────────────┬───────────────────┬───────────────────┬───────────────────┬───────────────┬───────────────┬───────────┬───────────────────────┬───────────────────┬───────────────────────┬───────────────────────────────┬───────────────────┬───────────────────┬───────────────────┬───────────┬───────────────────────┬───────────────┬───────────────┬───────────────┬──────────	*/
