/*	────────────────────────────┼───────────┼───────────┼───────────────────────┼───────────────┼───────────┼───────────────┼───────────────────────┼───────────────┼───────────────────┼───────────────────┼───────────────────┼───────────────┼───────────────┼───────────┼───────────────────────┼───────────────────┼───────────────────────┼───────────────────────────────┼───────────────────┼───────────────────┼───────────────────┼───────────┼───────────────────────┼───────────────┼───────────────┼───────────────┼──────────	*/
/*	201702011000000028135141	│	3585	│	1000	│	엔프롬정보기술(주)			│	20170101	│	2		│	20170101	│	엔프롬정보기술(주)			│	1111111222	│	31788173.0000	│	3178810.0000	│	34966983.0000	│	이준협			│	일반			│	10000	│	엔프롬정보기술(주)			│	030				│	100112312313		│	서울 강남구 청담동 123-11			│	민병진				│	02-633-5225		│	003				│	기업		│	193100				│	34773883	│	31597173	│	3176710		│	003		*/
/*	────────────────────────────┼───────────┼───────────┼───────────────────────┼───────────────┼───────────┼───────────────┼───────────────────────┼───────────────┼───────────────────┼───────────────────┼───────────────────┼───────────────┼───────────────┼───────────┼───────────────────────┼───────────────────┼───────────────────────┼───────────────────────────────┼───────────────────┼───────────────────┼───────────────────┼───────────┼───────────────────────┼───────────────┼───────────────┼───────────────┼──────────	*/
/*	2017010141000009c0003o07	│	3585	│	1000	│	용당세관				│	20170102	│	2		│	20170102	│	용당세관				│	1231231231	│	22000.0000		│	2200.0000		│	24200.0000		│	용당세관장		│	일반			│	10001	│	용당세관				│	110				│	21323123123123		│	경남 함양군 독계리 222				│	용당세관장			│	055-525-5225	│	011				│	농협중		│	1100				│	23100		│	21000		│	2100		│	003		*/
/*	────────────────────────────┼───────────┼───────────┼───────────────────────┼───────────────┼───────────┼───────────────┼───────────────────────┼───────────────┼───────────────────┼───────────────────┼───────────────────┼───────────────┼───────────────┼───────────┼───────────────────────┼───────────────────┼───────────────────────┼───────────────────────────────┼───────────────────┼───────────────────┼───────────────────┼───────────┼───────────────────────┼───────────────┼───────────────┼───────────────┼──────────	*/
/*	201702021000000029043804	│	3585	│	1000	│	(주)펌앤펌				│	20170103	│	2		│	20170103	│	(주)펌앤펌				│	1388160239	│	22000.0000		│	2200.0000		│	24200.0000		│	박매희			│	일반			│	10006	│	(주)펌앤펌				│	040				│	7777-77777-777777	│	충남 태안군 123-123				│	박매희				│	0419652584		│	004				│	국민		│	NULL				│	24200		│	22000		│	2200		│			*/
/*	────────────────────────────┴───────────┴───────────┴───────────────────────┴───────────────┴───────────┴───────────────┴───────────────────────┴───────────────┴───────────────────┴───────────────────┴───────────────────┴───────────────┴───────────────┴───────────┴───────────────────────┴───────────────────┴───────────────────────┴───────────────────────────────┴───────────────────┴───────────────────┴───────────────────┴───────────┴───────────────────────┴───────────────┴───────────────┴───────────────┴──────────	*/

/*	ETAX_TY_NM			> anguEtaxTaxDiv			*/
/*	ISS_DT 				> anguEtaxTaxWriteDate		*/
/*	ISU_DT 				> anguEtaxTaxIssueDate		*/
/*	TR_NM				> anguEtaxTaxPartnerName	*/
/*	TRREG_NB			> anguEtaxTaxPartnerNum		*/
/*	SUP_AM 				> anguEtaxTaxAmt			*/
/*	VAT_AM 				> anguEtaxTaxVat			*/
/*	SUM_AM 				> anguEtaxTaxReqAmt			*/
/*	ISS_NO 				> anguEtaxTaxNum			*/
/*	EXCUT_SUM_AMOUNT	> anguEtaxTaxUsedAmt		*/
/*	AM					> anguEtaxTaxNotUsedAmt		*/

/*	작성일자 : 20170101 ~ 20170921 ( DT_FG : 1 )			*/
/*	발행일자 : 20170101 ~ 20170921 ( DT_FG : 2 )			*/
/*	세무구분 : 과세 ( TAX_TY : 2 )							*/
/*	세무구분 : 면세 ( TAX_TY : 4 )							*/
/*	세금계산서분류 : 전체 ( ETAX_TY : '' )					*/
/*	세금계산서분류 : 일반 ( ETAX_TY : '1' )					*/
/*	세금계산서분류 : 일반 ( ETAX_TY : '2' )					*/
/*	매입거래처 : 거래처코드 ( TR_CD : 거래처코드 + '|' )	*/

DECLARE @RESULT TABLE(
	anguEtaxTaxNum NVARCHAR(30)
	, CO_CD NVARCHAR(4)
	, DIV_CD NVARCHAR(4)
	, DIV_NM NVARCHAR(50)
	, anguEtaxTaxWriteDate NVARCHAR(8)
	, TAX_TY NVARCHAR(1)
	, anguEtaxTaxIssueDate NVARCHAR(8)
	, anguEtaxTaxPartnerName NVARCHAR(60)
	, anguEtaxTaxPartnerNum NVARCHAR(100)
	, anguEtaxTaxAmt NUMERIC(17,4)
	, anguEtaxTaxVat NUMERIC(17,4)
	, anguEtaxTaxReqAmt NUMERIC(17,4)
	, DIVCEO_NM NVARCHAR(20)
	, anguEtaxTaxDiv NVARCHAR(10)
	, TR_CD NVARCHAR(10)
	, BCNC_CMPNY_NM NVARCHAR(300)
	, DUZON_BANK_CODE NVARCHAR(10)
	, BCNC_ACNUT_NO NVARCHAR(108)
	, BCNC_ADRES NVARCHAR(500)
	, BCNC_RPRSNTY_NM NVARCHAR(100)
	, BCNC_TELNO NVARCHAR(14)
	, BCNC_BANK_CODE NVARCHAR(3)
	, BANK_NM NVARCHAR(100)
	, anguEtaxTaxUsedAmt NUMERIC(17,4)
	, anguEtaxTaxNotUsedAmt NUMERIC(17,4)
	, SUP_M_SPLPC NUMERIC(17,4)
	, VAT_M_VAT NUMERIC(17,4)
	, SEND_YN NVARCHAR(3)
)

INSERT INTO @RESULT ( anguEtaxTaxNum, CO_CD, DIV_CD, DIV_NM, anguEtaxTaxWriteDate, TAX_TY, anguEtaxTaxIssueDate, anguEtaxTaxPartnerName, anguEtaxTaxPartnerNum, anguEtaxTaxAmt, anguEtaxTaxVat, anguEtaxTaxReqAmt, DIVCEO_NM, anguEtaxTaxDiv, TR_CD, BCNC_CMPNY_NM, DUZON_BANK_CODE, BCNC_ACNUT_NO, BCNC_ADRES, BCNC_RPRSNTY_NM, BCNC_TELNO, BCNC_BANK_CODE, BANK_NM, anguEtaxTaxUsedAmt, anguEtaxTaxNotUsedAmt, SUP_M_SPLPC, VAT_M_VAT, SEND_YN )
EXEC USP_ANGU300_ETAX_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD},@DT_FG=#{DT_FG},@FR_DT=#{FR_DT},@TO_DT=#{TO_DT},@TAX_TY='4',@ETAX_TY='${ETAX_TY}',@TR_CD=#{TR_CD},@DDTLBZ_ID=#{DDTLBZ_ID},@TASC_CMMN_DETAIL_CODE_NM='001'

SELECT	CONVERT ( NVARCHAR(10), ROW_NUMBER() OVER(ORDER BY anguEtaxTaxWriteDate, anguEtaxTaxIssueDate, anguEtaxTaxPartnerName) ) AS anguEtaxTaxNo
		, anguEtaxTaxNum
		, CO_CD
		, DIV_CD
		, DIV_NM
		, CONVERT ( NVARCHAR(10), CONVERT ( DATETIME, anguEtaxTaxWriteDate, 112 ), 121 ) AS anguEtaxTaxWriteDate
		, TAX_TY
		, CONVERT ( NVARCHAR(10), CONVERT ( DATETIME, anguEtaxTaxIssueDate, 112 ), 121 ) AS anguEtaxTaxIssueDate
		, anguEtaxTaxPartnerName
		, anguEtaxTaxPartnerNum
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, anguEtaxTaxAmt ), 1 ), '.00', '' ) AS anguEtaxTaxAmt
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, anguEtaxTaxVat ), 1 ), '.00', '' ) AS anguEtaxTaxVat
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, anguEtaxTaxReqAmt ), 1 ), '.00', '' ) AS anguEtaxTaxReqAmt
		, DIVCEO_NM
		, anguEtaxTaxDiv
		, TR_CD
		, BCNC_CMPNY_NM
		, DUZON_BANK_CODE
		, BCNC_ACNUT_NO
		, BCNC_ADRES
		, BCNC_RPRSNTY_NM
		, BCNC_TELNO
		, BCNC_BANK_CODE
		, BANK_NM
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, ISNULL(anguEtaxTaxUsedAmt, 0.0000) ), 1 ), '.00', '' ) AS anguEtaxTaxUsedAmt
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, anguEtaxTaxNotUsedAmt ), 1 ), '.00', '' ) AS anguEtaxTaxNotUsedAmt
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, SUP_M_SPLPC ), 1 ), '.00', '' ) AS SUP_M_SPLPC
		, REPLACE ( CONVERT ( VARCHAR, CONVERT ( MONEY, VAT_M_VAT ), 1 ), '.00', '' ) AS VAT_M_VAT
		, SEND_YN
FROM	@RESULT

/*	[iCUBE][MSSQL] AnguCode.getAnguCardAuthI_Select	*/
/*	국고보조통합시스템 - 카드권한 조회	*/

/*	EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_CARD_NM_SELECT'	*/
/*	EXEC USP_ANGU300_CARD_NM_SELECT @LANGKIND=N'KOR',@CO_CD=N'3585',@DDTLBZ_ID=N'20170125000000000787'	*/

DECLARE @RESULT TABLE(
	CTR_CD NVARCHAR(10)
	, TR_NM NVARCHAR(100)
	, BA_NB NVARCHAR(40)
	, REG_NB NVARCHAR(20)
	, CEO_NM NVARCHAR(60)
)

INSERT INTO @RESULT ( CTR_CD, TR_NM, BA_NB, REG_NB, CEO_NM )
EXEC USP_ANGU300_CARD_NM_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD},@DDTLBZ_ID=#{DDTLBZ_ID}

SELECT	CTR_CD
		, TR_NM
		, BA_NB
FROM	@RESULT

/* [iCUBE][MSSQL] AnguCode.getAnguCardInfoI_Select */ /* 국고보조통합시스템 - 카드사용내역 조회 */ /* 2018-10-15 iCUBE프로시저 파라미터에 따른 프로시저 결과 차이 확인 후 보완 */
/* EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_CARDINFO_CD_SELECT' */ /* EXEC USP_ANGU300_CARDINFO_CD_SELECT @LANGKIND=N'KOR',@CO_CD=N'3585',@CONFM_DE_FR=N'20170101',@CONFM_DE_TO=N'20171122',@CARD_NO=N'5525764197210327',@MRHST_NM=N'',@PUCHAS_TAMT=0,@DDTLBZ_ID=N'20170125000000000787',@TASC_CMMN_DETAIL_CODE_NM=N'001' */
DECLARE @RESULT TABLE( CONFM_DE NVARCHAR(8) , MRHST_NM NVARCHAR(50) , MRBCRB_REGIST_NO NVARCHAR(10) , SPLPC NUMERIC(15,0) , VAT NUMERIC(15,0) , PUCHAS_TAMT NUMERIC(15,0) , PUCHAS_TKBAK_NO NVARCHAR(32) , PUCHAS_DE NVARCHAR(8) , BCNC_LSFT_NO NVARCHAR(100) , BCNC_RPRSNTV_NM NVARCHAR(300) , BCNC_TELNO NVARCHAR(14) , TR_NM NVARCHAR(100) , CTR_CD NVARCHAR(10) , CARD_NO NVARCHAR(40) , BCNC_BANK_CODE NVARCHAR(3) , BANK_NM NVARCHAR(100) , TR_CD NVARCHAR(12) , BCNC_CMPNY_NM NVARCHAR(300) , CEO_NM NVARCHAR(50) , TEL NVARCHAR(64) , DUZON_BANK_CODE NVARCHAR(10) , BCNC_ACNUT_NO NVARCHAR(20) , BCNC_ADRES NVARCHAR(500) , BCNC_ADRES2 NVARCHAR(500) , EXCUT_SUM_AMOUNT NUMERIC(15,0) , AM NUMERIC(15,0) , SUP_M_SPLPC NUMERIC(15,0) , VAT_M_VAT NUMERIC(15,0) , PUCHAS_SE_CODE NVARCHAR(3) )
IF(#{TASC_CMMN_DETAIL_CODE_NM} != '003') BEGIN
INSERT INTO @RESULT ( CONFM_DE, MRHST_NM, MRBCRB_REGIST_NO, SPLPC, VAT, PUCHAS_TAMT, PUCHAS_TKBAK_NO, PUCHAS_DE, BCNC_LSFT_NO, BCNC_RPRSNTV_NM, BCNC_TELNO, TR_NM, CTR_CD, CARD_NO, BCNC_BANK_CODE, BANK_NM, TR_CD, BCNC_CMPNY_NM, CEO_NM, TEL, DUZON_BANK_CODE, BCNC_ACNUT_NO, BCNC_ADRES, BCNC_ADRES2, EXCUT_SUM_AMOUNT, AM, SUP_M_SPLPC, VAT_M_VAT, PUCHAS_SE_CODE ) EXEC USP_ANGU300_CARDINFO_CD_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD},@CONFM_DE_FR=#{CONFM_DE_FR},@CONFM_DE_TO=#{CONFM_DE_TO},@CARD_NO=#{CARD_NO},@MRHST_NM=#{MRHST_NM},@PUCHAS_TAMT=#{PUCHAS_TAMT},@DDTLBZ_ID=#{DDTLBZ_ID},@TASC_CMMN_DETAIL_CODE_NM=#{TASC_CMMN_DETAIL_CODE_NM}
SELECT CONFM_DE AS anguEtaxTaxIssueDate /* 거래일자 */ , MRHST_NM AS anguEtaxTaxPartnerName /* 가맹점명 */ , MRBCRB_REGIST_NO AS mrbcrbRegistNo , SPLPC AS anguEtaxTaxAmt /* 공급가액 */ , VAT AS anguEtaxTaxVat /* 부가세 */ , PUCHAS_TAMT AS anguEtaxTaxReqAmt /* 총금액 */ , PUCHAS_TKBAK_NO AS anguEtaxTaxNum /* 카드승인번호 */ , PUCHAS_DE AS puchasDe , BCNC_LSFT_NO AS anguEtaxTaxPartnerNum /* 가맹점사업자번호 */ , BCNC_RPRSNTV_NM AS bcncRprsntvNm , BCNC_TELNO AS bcncTelNo , TR_NM AS trNm /* 카드명 */ , CTR_CD AS ctrCd , CARD_NO AS cardNo /* 카드번호 */ , ISNULL(BCNC_BANK_CODE, '') AS bcncBankCode , ISNULL(BANK_NM, '') AS bankNm , ISNULL(TR_CD, '') AS trCd , ISNULL(BCNC_CMPNY_NM, '') AS bcncCmpnyNm , ISNULL(CEO_NM, '') AS ceoNm , ISNULL(TEL, '') AS tel , ISNULL(DUZON_BANK_CODE, '') AS duzonBankCode , ISNULL(BCNC_ACNUT_NO, '') AS bcncAcnutNo , BCNC_ADRES AS bcncAdres , BCNC_ADRES2 AS bcncAdres2 , EXCUT_SUM_AMOUNT AS excutSumAmount /* 기사용금액 */ , AM AS am /* 등록가능공급가액 */ , SUP_M_SPLPC AS SUP_M_SPLPC , VAT_M_VAT AS VAT_M_VAT , PUCHAS_SE_CODE FROM @RESULT END ELSE IF (#{TASC_CMMN_DETAIL_CODE_NM} = '003') BEGIN
INSERT INTO @RESULT ( CONFM_DE, MRHST_NM, MRBCRB_REGIST_NO, SPLPC, VAT, PUCHAS_TAMT, PUCHAS_TKBAK_NO, PUCHAS_DE, BCNC_LSFT_NO, BCNC_RPRSNTV_NM, BCNC_TELNO, TR_NM, CARD_NO, BCNC_ACNUT_NO, DUZON_BANK_CODE, BCNC_BANK_CODE, BANK_NM, TR_CD, BCNC_CMPNY_NM, CEO_NM, TEL, BCNC_ADRES, BCNC_ADRES2, EXCUT_SUM_AMOUNT, AM, SUP_M_SPLPC, VAT_M_VAT, PUCHAS_SE_CODE ) EXEC USP_ANGU300_CARDINFO_CD_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD},@CONFM_DE_FR=#{CONFM_DE_FR},@CONFM_DE_TO=#{CONFM_DE_TO},@CARD_NO=#{CARD_NO},@MRHST_NM=#{MRHST_NM},@PUCHAS_TAMT=#{PUCHAS_TAMT},@DDTLBZ_ID=#{DDTLBZ_ID},@TASC_CMMN_DETAIL_CODE_NM=#{TASC_CMMN_DETAIL_CODE_NM}
SELECT CONFM_DE AS anguEtaxTaxIssueDate /* 거래일자 */ , MRHST_NM AS anguEtaxTaxPartnerName /* 가맹점명 */ , MRBCRB_REGIST_NO AS mrbcrbRegistNo , SPLPC AS anguEtaxTaxAmt /* 공급가액 */ , VAT AS anguEtaxTaxVat /* 부가세 */ , PUCHAS_TAMT AS anguEtaxTaxReqAmt /* 총금액 */ , PUCHAS_TKBAK_NO AS anguEtaxTaxNum /* 카드승인번호 */ , PUCHAS_DE AS puchasDe , BCNC_LSFT_NO AS anguEtaxTaxPartnerNum /* 가맹점사업자번호 */ , BCNC_RPRSNTV_NM AS bcncRprsntvNm , BCNC_TELNO AS bcncTelNo , TR_NM AS trNm /* 카드명 */ , CARD_NO AS cardNo /* 카드번호 */ , ISNULL(BCNC_BANK_CODE, '') AS bcncBankCode , ISNULL(BANK_NM, '') AS bankNm , ISNULL(TR_CD, '') AS trCd , ISNULL(BCNC_CMPNY_NM, '') AS bcncCmpnyNm , ISNULL(CEO_NM, '') AS ceoNm , ISNULL(TEL, '') AS tel , ISNULL(DUZON_BANK_CODE, '') AS duzonBankCode , ISNULL(BCNC_ACNUT_NO, '') AS bcncAcnutNo , BCNC_ADRES AS bcncAdres , BCNC_ADRES2 AS bcncAdres2 , EXCUT_SUM_AMOUNT AS excutSumAmount /* 기사용금액 */ , AM AS am /* 등록가능공급가액 */ , SUP_M_SPLPC AS SUP_M_SPLPC , VAT_M_VAT AS VAT_M_VAT , PUCHAS_SE_CODE FROM @RESULT END

/* [iCUBE][MSSQL] AnguCode.getAnguEmpInfoI_Select */ /* 국고보조통합시스템 - 사원 조회 */
/* EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_SEMP_CD_SELECT' */
/* USP_GET_HELPCODE : result sample */ /* ────────┬───────────┬───────────┬───────────┬───────────────────┬───────────────┬───────────┬───────────────────────────────────────────┬───────────────────────────┬───────┬───────────────────┬─────────── */ /* CO_CD │ EMP_CD │ KOR_NM │ KOR_NMK │ DUZON_BANK_CODE │ ACCT_NO │ DPST_NM │ RSRG_NO │ RSRG_ADD │ TEL │ BCNC_BANK_CODE │ BANK_NM */ /* ────────┼───────────┼───────────┼───────────┼───────────────────┼───────────────┼───────────┼───────────────────────────────────────────┼───────────────────────────┼───────┼───────────────────┼─────────── */ /* 3585 │ 1000 │ 김재단 │ 김재단 │ 010 │ 77777777 │ 김재단 │ 7000001!@1@!dpHXJheRFHAaxTvxof1NeA== │ 서울시 강남구 대치동 │ │ 001 │ 한국은행 */ /* ────────┴───────────┴───────────┴───────────┴───────────────────┴───────────────┴───────────┴───────────────────────────────────────────┴───────────────────────────┴───────┴───────────────────┴─────────── */
DECLARE @RESULT TABLE( CO_CD NVARCHAR(4) , EMP_CD NVARCHAR(12) , KOR_NM NVARCHAR(40) , KOR_NMK NVARCHAR(30) , DUZON_BANK_CODE NVARCHAR(10) , ACCT_NO NVARCHAR(50) , DPST_NM NVARCHAR(60) , RSRG_NO NVARCHAR(100) , RSRG_ADD NVARCHAR(80) , TEL NVARCHAR(20) , BCNC_BANK_CODE NVARCHAR(3) , BANK_NM NVARCHAR(100) )
INSERT INTO @RESULT ( CO_CD, EMP_CD, KOR_NM, KOR_NMK, DUZON_BANK_CODE, ACCT_NO, DPST_NM, RSRG_NO, RSRG_ADD, TEL, BCNC_BANK_CODE, BANK_NM ) EXEC USP_ANGU300_SEMP_CD_SELECT @LANGKIND=N'KOR',@CO_CD=#{erpCompSeq}
SELECT CO_CD , EMP_CD AS TR_CD , ISNULL(KOR_NM, '') AS anguEtaxTaxPartnerName , ISNULL(KOR_NM, '') AS BCNC_RPRSNTY_NM , ISNULL(DUZON_BANK_CODE, '') AS DUZON_BANK_CODE , ISNULL(ACCT_NO, '') AS BCNC_ACNUT_NO , ISNULL(DPST_NM, '') AS BOJO_RMK , ISNULL(RSRG_NO, '') AS anguEtaxTaxPartnerNum , ISNULL(RSRG_ADD, '') AS BCNC_ADRES , ISNULL(TEL, '') AS BCNC_TELNO , ISNULL(BCNC_BANK_CODE, '') AS BCNC_BANK_CODE , ISNULL(BANK_NM, '') AS BANK_NM FROM @RESULT

