/*	국고보조통합시스템 - 거래처 조회 - 계좌정보	*/

/*	EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_STRADE_CD_SELECT'	*/

/*	USP_ANGU300_STRADE_CD_SELECT : result sample	*/

/*	────────────┬───────────────┬───────────────────────────┬───────────────┬───────────┬───────────	*/
/*	DEPOSITOR	│	REG_NB		│	ADDR					│	TEL			│	BANK_CD	│	BANK_NM		*/
/*	────────────┼───────────────┼───────────────────────────┼───────────────┼───────────┼───────────	*/
/*	NULL		│	6018132551	│	경기 수원시 팔당구 332			│	0312221112	│	003		│	기업은행		*/
/*	────────────┴───────────────┴───────────────────────────┴───────────────┴───────────┴───────────	*/

DECLARE @RESULT TABLE(
	DEPOSITOR NVARCHAR(100)
	, REG_NB NVARCHAR(100)
	, ADDR NVARCHAR(301)
	, TEL NVARCHAR(64)
	, BANK_CD NVARCHAR(50)
	, BANK_NM NVARCHAR(100)
)

INSERT INTO @RESULT ( DEPOSITOR, REG_NB, ADDR, TEL, BANK_CD, BANK_NM )
EXEC USP_ANGU300_STRADE_CD_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD},@TR_CD=#{TR_CD},@JIRO_CD=#{JIRO_CD},@TR_FG=#{TR_FG}

SELECT	DEPOSITOR AS DEPOSITOR /* 예금주 */
		, REG_NB AS anguEtaxTaxPartnerNum /* 거래처 명 */
		, ADDR AS BCNC_ADRES /* 거래처 주소 */
		, TEL AS BCNC_TELNO /* 거래처 전화번호 */
		, BANK_CD AS BCNC_BANK_CODE /* 은행 코드 */
		, BANK_NM AS BANK_NM /* 은행 명 */
FROM	@RESULT

/*	[iCUBE][MSSQL] AnguCode.getAnguG20BankInfoI_Select_01	*/
/*	국고보조통합시스템 - 금융기관 조회 - iCUBE G20 */

/*	EXEC SP_SPHELP @P_SPNAME='USP_GET_HELPCODE'	*/

/*	USP_GET_HELPCODE : result sample	*/
/*	────────────────┬───────────┬───────────	*/
/*	DUZON_BANK_CODE	│	BANK_NM	│	BANK_NMK	*/
/*	────────────────┼───────────┼───────────	*/
/*	010				│	한국		│	한국			*/
/*	────────────────┼───────────┼───────────	*/
/*	030				│	기업		│	기업			*/
/*	────────────────┼───────────┼───────────	*/
/*	040				│	국민		│	국민			*/
/*	────────────────┴───────────┴───────────	*/

DECLARE @RESULT TABLE(
	BANK_CD NVARCHAR(10)
	, BANK_NM NVARCHAR(100)
	, BANK_NMK NVARCHAR(120)
)

INSERT INTO @RESULT ( BANK_CD, BANK_NM, BANK_NMK )
EXEC USP_GET_HELPCODE @LANGKIND=#{LANGKIND},@P_HELP_TY=#{P_HELP_TY},@P_CO_CD=#{P_CO_CD},@P_CODE=#{P_CODE},@P_CODE2=#{P_CODE2},@P_CODE3=#{P_CODE3},@P_USE_YN=#{P_USE_YN},@P_NAME=#{P_NAME},@P_STD_DT=#{P_STD_DT},@P_WHERE=#{P_WHERE}

SELECT	BANK_CD AS DUZON_BANK_CODE
		, BANK_NM AS BANK_NM
		, BANK_NMK
FROM	@RESULT

/*	[iCUBE][MSSQL] AnguCode.getAnguG20BankInfoI_Select_02	*/
/*	국고보조통합시스템 - 금융기관 조회 - e나라도움 */

/*	EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_BANK_CD_SELECT'	*/

