DECLARE @RESULT TABLE(
	BANK_CD NVARCHAR(10)
	, SBANK_NM NVARCHAR(20)
)

INSERT INTO @RESULT ( BANK_CD, SBANK_NM )
EXEC USP_ANGU300_BANK_CD_SELECT @LANGKIND=N'KOR',@CO_CD=#{CO_CD},@BANK_CD=#{BANK_CD}

SELECT	BANK_CD AS BCNC_BANK_CODE
		, SBANK_NM AS BANK_NM
FROM	@RESULT

		/*	[iCUBE][MSSQL] AnguCode.getAnguBimokInfoI_Select	*/
