		/*	────────────────────────────┼───────────────────┼───────────────────	*/
		/*	32001						│	민간이전			│	민간경상보조				*/
		/*	────────────────────────────┼───────────────────┼───────────────────	*/
		/*	43001						│	유형자산			│	자산취득비				*/
		/*	────────────────────────────┴───────────────────┴───────────────────	*/

		/*	ASSTN_EXPITM_TAXITM_CODE	> subDetailBimokCode	( 보조비목세목코드 )	*/
		/*	ASSTN_EXPITM_NM				> subBimokName			( 보조비목명 )		*/
		/*	ASSTN_TAXITM_NM				> subDetailBimokName	( 보조세목명 )		*/
		
		/*	DECLARE @RESULT TABLE(						*/
		/*		ASSTN_EXPITM_TAXITM_CODE NVARCHAR(5)	*/
		/*		, ASSTN_EXPITM_NM NVARCHAR(100)			*/
		/*		, ASSTN_TAXITM_NM NVARCHAR(300)			*/
		/*	)											*/
		
		/*	INSERT INTO @RESULT ( ASSTN_EXPITM_TAXITM_CODE, ASSTN_EXPITM_NM, ASSTN_TAXITM_NM )											*/
		/*	EXEC USP_ANGU300_ASSTN_CD_SELECT @LANGKIND=N'KOR',@CO_CD=N'3585',@MOSF_GISU_DT=N'20171008',@MOSF_GISU_SQ=1,@FSYR=N'2017'	*/
		
		/*	SELECT	ASSTN_EXPITM_TAXITM_CODE AS subDetailBimokCode	*/
		/*			, ASSTN_EXPITM_NM AS subBimokName				*/
		/*			, ASSTN_TAXITM_NM AS subDetailBimokName			*/
		/*	FROM	@RESULT											*/
		
		SELECT	C.ASSTN_EXPITM_TAXITM_CODE AS subDetailBimokCode
				, C.ASSTN_EXPITM_NM AS subBimokName
				, C.ASSTN_TAXITM_NM AS subDetailBimokName
		FROM	ANBOJO_DDTLBZINFO_PFRS B
				LEFT OUTER JOIN ANBOJO_SBGTCDINFO C ON B.ASSTN_TAXITM_CODE = C.ASSTN_EXPITM_TAXITM_CODE
		WHERE	B.BSNSYEAR = #{BSNSYEAR}
		AND		B.DDTLBZ_ID = #{DDTLBZ_ID}
		AND		C.FSYR = #{FSYR}
		AND		C.ASSTN_EXPITM_TAXITM_SE_CODE = '002'
		GROUP	BY C.ASSTN_EXPITM_TAXITM_CODE, C.ASSTN_EXPITM_NM, C.ASSTN_TAXITM_NM
		ORDER	BY C.ASSTN_EXPITM_TAXITM_CODE, C.ASSTN_EXPITM_NM, C.ASSTN_TAXITM_NM

/*	[iCUBE][MSSQL] AnguCode.getAnguJaewonInfoI_Select	*/
/*	국고보조통합시스템 - 재원구분 조회	*/
				
/*	EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_ANBOJO_B_CMMN_CODE_SELECT'	*/
				
/*	USP_ANGU300_ANBOJO_B_CMMN_CODE_SELECT : result sample	*/
/*	────────────┬───────────────────┬───────────────────────┬───────────────────────	*/
/*	CMMN_CODE	│	CMMN_CODE_NM	│	CMMN_DETAIL_CODE	│	CMMN_DETAIL_CODE_NM		*/
/*	────────────┼───────────────────┼───────────────────────┼───────────────────────	*/
/*	1220		│	재원구분코드			│	001					│	보조금						*/
/*	────────────┴───────────────────┴───────────────────────┴───────────────────────	*/

/*	CMMN_DETAIL_CODE > anguJaewonDivCode		*/
/*	CMMN_DETAIL_CODE_NM > anguJaewonDivName	*/

DECLARE @RESULT TABLE(
	CMMN_CODE NVARCHAR(10)
	, CMMN_CODE_NM NVARCHAR(200)
	, CMMN_DETAIL_CODE NVARCHAR(10)
	, CMMN_DETAIL_CODE_NM NVARCHAR(200)
)

/*	INSERT INTO @RESULT ( CMMN_CODE, CMMN_CODE_NM, CMMN_DETAIL_CODE, CMMN_DETAIL_CODE_NM )												*/
/*	EXEC USP_ANGU300_ANBOJO_B_CMMN_CODE_SELECT @LANGKIND=N'KOR',@CO_CD=N'3585',@MOSF_GISU_DT=N'20171008',@MOSF_GISU_SQ=1,@MOSF_BG_SQ=1	*/

SELECT	/*	D.CMMN_CODE			*/
		/*	, E.CMMN_CODE_NM	*/
		D.CMMN_DETAIL_CODE AS anguJaewonDivCode
		, D.CMMN_DETAIL_CODE_NM AS anguJaewonDivName
FROM	(
			SELECT	BSNSYEAR
					, DDTLBZ_ID
					, ASSTN_TAXITM_CODE
					, FNRSC_SE_CODE
			FROM	ANBOJO_DDTLBZINFO_PFRS
			WHERE	DDTLBZ_ID = #{DDTLBZ_ID}
			AND		BSNSYEAR = #{BSNSYEAR}
			AND		ASSTN_TAXITM_CODE = #{ASSTN_TAXITM_CODE}
		) C LEFT OUTER JOIN ANBOJO_CMMN_CODE_D D ON D.SYS_SE_CODE = '001' AND D.CMMN_CODE = '1220' AND C.FNRSC_SE_CODE = D.CMMN_DETAIL_CODE
		LEFT OUTER JOIN ANBOJO_CMMN_CODE E ON D.SYS_SE_CODE = E.SYS_SE_CODE AND D.CMMN_CODE = E.CMMN_CODE

/*	[iCUBE][MSSQL] AnguCode.getAnguResultAmtInfoI_Select	*/
/*	국고보조통합시스템 - 국고보조 집행 금액 조회	*/
						
/*	EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_RESULTERP_SELECT'	*/
						
/*	USP_ANGU300_RESULTERP_SELECT : result sample	*/
/*	────────────────────┬───────────────────┬───────────────┬───────────────┬───────────────	*/
/*	EXCUT_PLAN_AMOUNT	│	EXCUT_AMOUNT	│	SUM_AMOUNT	│	SUM			│	SUB				*/
/*	────────────────────┼───────────────────┼───────────────┼───────────────┼───────────────	*/
/*	10000000			│	5000000			│	6853100		│	11853100	│	-1853100		*/
/*	────────────────────┴───────────────────┴───────────────┴───────────────┴───────────────	*/

/*	EXCUT_PLAN_AMOUNT	>	excutPlanAmount		( 계획금액 )			*/
/*	EXCUT_AMOUNT		>	excutAmount			( E나라도움 집행금액 )	*/
/*	SUM_AMOUNT			>	sumAmount			( 미등록 집행금액 )		*/
/*	SUM					>	sum					( 집행금액 )			*/
/*	SUB					>	sub					( 집행잔액 )			*/

DECLARE @RESULT TABLE(
	EXCUT_PLAN_AMOUNT NUMERIC(15, 0)
	, EXCUT_AMOUNT NUMERIC(15, 0)
	, SUM_AMOUNT NUMERIC(15, 0)
	, SUM NUMERIC(15, 0)
	, SUB NUMERIC(15, 0)
)

INSERT INTO @RESULT ( EXCUT_PLAN_AMOUNT, EXCUT_AMOUNT, SUM_AMOUNT, SUM, SUB )
EXEC USP_ANGU300_RESULTERP_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD},@BSNSYEAR=#{BSNSYEAR},@DDTLBZ_ID=#{DDTLBZ_ID},@ASSTN_TAXITM_CODE=#{ASSTN_TAXITM_CODE},@FNRSC_SE_CODE=#{FNRSC_SE_CODE}

SELECT	EXCUT_PLAN_AMOUNT AS excutPlanAmount
		, EXCUT_AMOUNT AS excutAmount
		, SUM_AMOUNT AS sumAmount
		, SUM AS sum
		, SUB AS sub
FROM	@RESULT

/*	[iCUBE][MSSQL] AnguCode.getAnguProjectInfoI_Select	*/
/*	국고보조통합시스템 - 국고보조 프로젝트 조회	*/

/*	EXEC SP_SPHELP @P_SPNAME='USP_ANGU300_MGT_CD_SELECT'	*/

/*	USP_ANGU300_MGT_CD_SELECT : result sample	*/
/*	EXEC USP_ANGU300_MGT_CD_SELECT @LANGKIND=N'KOR',@CO_CD=N'3585',@DDTLBZ_ID=N'20170125000000000787'	*/
/*	────────┬───────────────────────┬───────────┬───────────	*/
/*	MGT_CD	│	PJT_NM				│	TR_CD	│	TR_NM		*/
/*	────────┼───────────────────────┼───────────┼───────────	*/
/*	00001	│	더존프로젝트1(한국보육)		│	NULL	│	NULL		*/
/*	────────┴───────────────────────┴───────────┴───────────	*/

/*	MGT_CD	>	mgtCode		( 프로젝트 코드 )		*/
/*	PJT_NM	>	pjtName		( 프로젝트 명 )		*/
/*	TR_CD	>	TR_CD		( 거래처 코드 )		*/
/*	TR_NM	>	TR_NM		( 거래처 명 )		*/

DECLARE @RESULT TABLE(
	MGT_CD NVARCHAR(10)
	, PJT_NM NVARCHAR(100)
	, TR_CD NVARCHAR(60)
	, TR_NM NVARCHAR(100)
)

INSERT INTO @RESULT ( MGT_CD, PJT_NM, TR_CD, TR_NM )
EXEC USP_ANGU300_MGT_CD_SELECT @LANGKIND=#{LANGKIND},@CO_CD=#{CO_CD},@DDTLBZ_ID=#{DDTLBZ_ID}

SELECT	MGT_CD AS mgtCode
		, PJT_NM AS pjtName
		, ISNULL(TR_CD, '') AS TR_CD
		, ISNULL(TR_NM, '') AS TR_NM
FROM	@RESULT
