	select 'DeleteCMS SUCCES'

sqlMap.delete:

/* 표준적요 초기화 기능 */ 
DELETE FROM $DB_NEOS$t_ex_summary
WHERE comp_seq = #compSeq#


/* 증빙유형 초기화 기능 */ 
DELETE FROM $DB_NEOS$t_ex_auth
WHERE comp_seq = #compSeq#

sqlMap.select:

/* !  [G20] 배포기능 / 고객사 버전 확인 */
/* DevMgrBuildAssistSQL.xml - CheckGwVersion */
	SELECT	SETUP_VERSION AS version
	FROM	$DB_NEOS$T_CO_GROUP


/* !  [G20] 배포기능 / 고객사 ERP 확인 */
/* DevMgrBuildAssistSQL.xml - CheckERP */
	SELECT 	COUNT(*) AS erpNum
		, t.erp	 AS erpName
	FROM
	(
	SELECT	DISTINCT erp_type_code AS erp
	FROM	$DB_NEOS$T_CO_ERP
	) t

sqlMap.insert:
/* ! (비영리 1.0) 참조품의 권한 설정 */ /* DevMgrBuildAssistSQL.xml - InsertConfferAuth */
INSERT INTO $DB_NEOS$g20_cons_auth (group_seq, emp_seq, gbn_org, seq, create_seq) SELECT group_seq , emp_seq , #auth_type# AS 'gbn_org'
, #auth_obj_seq# AS 'seq'
, #auth_obj_seq# AS 'seq'
, comp_seq AS 'seq'
, 'SYSTEM' AS 'create_seq' FROM $DB_NEOS$v_user_info where emp_seq = #auth_user_seq# and dept_seq = #auth_user_dept#;

sqlMap.delete:
/* ! (비영리 1.0) 참조품의 권한 설정  삭제*/
/* DevMgrBuildAssistSQL.xml - DeleteConfferAuth */
DELETE FROM $DB_NEOS$g20_cons_auth
WHERE auth_seq 	= #auth_seq#

sqlMap.select:
/* ! (비영리 1.0) 참조품의 권한 설정 조회 */ /* DevMgrBuildAssistSQL.xml - SelectConfferAuth */
SELECT auth.auth_seq, auth.emp_seq, auth.gbn_org, auth.seq , CASE auth.gbn_org WHEN 'c' THEN '회사' WHEN 'd' THEN '부서' ELSE '사용자' END as gbn_name , CASE auth.gbn_org WHEN 'c' THEN ( select comp_name from $DB_NEOS$t_co_comp_multi where comp_seq = auth.seq and lang_code = 'kr' ) WHEN 'd' THEN ( select dept_name from $DB_NEOS$t_co_dept_multi where dept_seq = auth.seq and lang_code = 'kr' ) ELSE ( select emp_name from $DB_NEOS$t_co_emp_multi where emp_seq = auth.seq and lang_code = 'kr' ) END as seq_name , ( select emp_name from $DB_NEOS$t_co_emp_multi where emp_seq = auth.emp_seq and lang_code = 'kr' ) as emp_name FROM $DB_NEOS$g20_cons_auth auth
WHERE emp_seq in ( select emp_seq from $DB_NEOS$t_co_emp_multi where emp_name like '%$searchCode$%' and lang_code = 'kr' )

